mapper.select:
		SELECT * 
		FROM PDSCI.SCH_ROTATION_DEPT_REQ
		WHERE RECORD_STATUS = 'Y'
		ROTATION_FLOW IN

  			#{rotationFlow}

 		AND REC_TYPE_ID = #{recTypeId}

 			AND ITEM_NAME LIKE '%${itemName}%'

		AND STANDARD_DEPT_ID IN

  			#{schDeptFlow}

SELECT SR.ROTATION_FLOW||SRD.GROUP_FLOW||SRD.STANDARD_DEPT_ID||SRDR.REC_TYPE_ID REQ_KEY,SUM(REQ_NUM) REQ_SUM FROM PDSCI.SCH_ROTATION_DEPT_REQ SRDR,PDSCI.SCH_ROTATION_DEPT SRD,PDSCI.SCH_ROTATION SR WHERE SRDR.RECORD_STATUS = 'Y' AND SRD.RECORD_STATUS = 'Y' AND SR.RECORD_STATUS = 'Y' AND SRDR.REL_RECORD_FLOW = SRD.RECORD_FLOW AND SRD.ROTATION_FLOW = SR.ROTATION_FLOW
AND SR.ROTATION_FLOW IN
#{rotationFlow}
AND SRD.STANDARD_DEPT_ID IN
#{standardDeptId}
GROUP BY SR.ROTATION_FLOW,SRD.GROUP_FLOW,SRD.STANDARD_DEPT_ID,SRDR.REC_TYPE_ID
